        if (req.query.access_token) {
                    passport.authenticate('bearer', function(err, user) {
                if (err || !user) {
                    return res.send(401, {
                        message: 'User is not logged in'
                    });
                }
                req.login(user, function(err) {
                    if (err) {
                        return res.send(401, {
                            message: 'User is not logged in'
                        });
                    }
                                    next();
                });
            })( req, res, next );
        } else {
            next();
        }
    }); ```

